        public void databaseLoad()
        {
            itemDatabaseIndex = 0;
            for (int i = 0; i < itemIcons.Count; i++)
                itemIcons[i].Dispose();
            itemIcons.Clear();
            foreach (string iconPath in Directory.EnumerateFiles(Directory.GetCurrentDirectory() + "\\Icons\\" + Convert.ToString(currentUIScale) + "\\"))
                itemIcons.Add((Bitmap)Image.FromFile(iconPath));
            string[] temp = File.ReadAllLines(Directory.GetCurrentDirectory() + "\\Position Settings.csv");
            if (Convert.ToBoolean(temp[5].Split(',')[1]) == false)
            {
                needsAlignment = true;
                changeAnalyzeButton(true);
            }
            int count = 0;
            while (count < Screen.AllScreens.Length)
            {
                if (temp[7] == Screen.AllScreens[count].DeviceName)
                {
                    activeScreen = Screen.AllScreens[count];
                    break;
                }
                count++;
            }
            using (StreamReader reader = new StreamReader(Directory.GetCurrentDirectory() + "\\Item Attributions.csv"))
            {
                while (reader.EndOfStream == false)
                {
                    string[] tempAttributes = new string[5];
                    tempAttributes = reader.ReadLine().Split(',');
                    itemID[itemDatabaseIndex] = Convert.ToInt32(tempAttributes[0]);
                    itemName[itemDatabaseIndex] = tempAttributes[1];
                    itemLocked[itemDatabaseIndex] = Convert.ToBoolean(tempAttributes[2]);
                    itemIgnored[itemDatabaseIndex] = Convert.ToBoolean(tempAttributes[3]);
                    itemProblematic[itemDatabaseIndex] = Convert.ToBoolean(tempAttributes[4]);
                    itemDatabaseIndex++;
                }
            }
            hStepSum = 0;
            vStepSum = 0;
            using (StreamReader reader = new StreamReader(Directory.GetCurrentDirectory() + "\\Position Settings.csv"))
            {
                string[] tempAttribute;
                tempAttribute = reader.ReadLine().Split(',');
                cW = Convert.ToInt32(tempAttribute[1]);
                tempAttribute = reader.ReadLine().Split(',');
                cH = Convert.ToInt32(tempAttribute[1]);
                tempAttribute = reader.ReadLine().Split(',');
                for (int i = 0; i < 7; i++)
                {
                    hs[i] = Convert.ToInt32(tempAttribute[i + 1]);
                    hStepSum += hs[i];
                }
                tempAttribute = reader.ReadLine().Split(',');
                for (int i = 0; i < 7; i++)
                {
                    vs[i] = Convert.ToInt32(tempAttribute[i + 1]);
                    vStepSum += vs[i];
                }
                tempAttribute = reader.ReadLine().Split(',');
                picW = Convert.ToInt32(tempAttribute[1]);
            }
            Bitmap testHeight = (Bitmap)Image.FromFile(Directory.GetCurrentDirectory() + "\\Icons\\" + Convert.ToString(currentUIScale) + "\\0.bmp");
            picH = testHeight.Height;
            testHeight.Dispose();
            int tempI = 0, tempJ = 0, hStepCounter, vStepCounter = 0;
            for (int row = cH; row <= vStepSum + cH; row += 0)
            {
                hStepCounter = 0;
                for (int col = cW; col <= hStepSum + cW; col += 0)
                {
                    itemCoords[tempI, tempJ] = new Rectangle(col, row, picW, picH);
                    tempJ++;
                    if (hStepCounter <= 6)
                    {
                        col += hs[hStepCounter];
                        hStepCounter++;
                    }
                    else
                        col++;
                }
                tempJ = 0;
                tempI++;
                if (vStepCounter <= 6)
                {
                    row += vs[vStepCounter];
                    vStepCounter++;
                }
                else
                    row++;
            }
            tempI = 0;
            tempJ = 0;
            vStepCounter = 0;
            for (int row = cH + picH; row <= vStepSum + cH + picH; row += 0)
            {
                hStepCounter = 0;
                for (int col = cW; col <= hStepSum + cW; col += 0)
                {
                    stack[tempI, tempJ] = new Rectangle(col, row, picW, picW - picH);
                    tempJ++;
                    if (hStepCounter <= 6)
                    {
                        col += hs[hStepCounter];
                        hStepCounter++;
                    }
                    else
                        col++;
                }
                tempJ = 0;
                tempI++;
                if (vStepCounter <= 6)
                {
                    row += vs[vStepCounter];
                    vStepCounter++;
                }
                else
                    row++;
            }
        }

