                                    found = false;
                                    break;
                                }
                            if (found == true)
                                for (int irow = row; irow < row + 50; irow++)
                                    if (capture.GetPixel(col, irow).R != 36 || capture.GetPixel(col, irow).G != 36 || capture.GetPixel(col, irow).B != 39)
                                    {
                                        found = false;
                                        break;
                                    }
                            if (found == true)
                            {
                                topRightX = col;
                                topY = row;
                                goto FoundTheInventory;
                            }
                        }
                    }
                }
                if (topRightX == -1 && topY == -1)
                {
                    if (MessageBox.Show("Couldn't find your inventory!\nPlease make sure your inventory is open.", "Inventory not found", MessageBoxButtons.RetryCancel, MessageBoxIcon.Error) == DialogResult.Cancel)
                        return false;
                    else
                        goto RetryFindingInventory;
                }
            FoundTheInventory:
                //finding the top left corner of the inventory
                topLeftX = topRightX;
                while (true)
                {
                    if (capture.GetPixel(topLeftX, topY).R != 36 || capture.GetPixel(topLeftX, topY).G != 36 || capture.GetPixel(topLeftX, topY).B != 39)
                    {
                        topLeftX++;
                        break;
                    }
                    topLeftX--;
                }
                //finding the slot by going diagonally down
                int iconTopLeftX = topLeftX, iconTopY = topY;
                while (true)
                {
                    iconTopLeftX++;
                    iconTopY++;
                    if (capture.GetPixel(iconTopLeftX, iconTopY).R != 36 || capture.GetPixel(iconTopLeftX, iconTopY).G != 36 || capture.GetPixel(iconTopLeftX, iconTopY).B != 39)
                        break;
                }
                //finding the left-most pixel of the slot
                while (true)
                {
                    iconTopLeftX--;
                    if (capture.GetPixel(iconTopLeftX, iconTopY).R == 36 && capture.GetPixel(iconTopLeftX, iconTopY).G == 36 && capture.GetPixel(iconTopLeftX, iconTopY).B == 39)
                    {
                        iconTopLeftX++;
                        break;
                    }
                }
                //finding the total height of the slot
                int iconTotalHeight;
                int counter = iconTopY;
                while (true)
                {
                    counter++;
                    if (capture.GetPixel(iconTopLeftX, counter).R == 36 && capture.GetPixel(iconTopLeftX, counter).G == 36 && capture.GetPixel(iconTopLeftX, counter).B == 39)
                    {
                        counter--;
                        iconTotalHeight = counter - iconTopY + 1;
                        break;
                    }
                }
                int[] vSteps = new int[7];
                int previousLastPixel = iconTopY;
                for (int i = 0; i < 7; i++)
                {
                    //using counter to get all the vertical steps
                    while (true)
                    {
                        counter++;
                        if (capture.GetPixel(iconTopLeftX, counter).R != 36 || capture.GetPixel(iconTopLeftX, counter).G != 36 || capture.GetPixel(iconTopLeftX, counter).B != 39)
                        {
                            vSteps[i] = counter - previousLastPixel;
                            previousLastPixel = counter;
                            break;
                        }
                    }
                    while (true)
                    {
                        counter++;
                        if (capture.GetPixel(iconTopLeftX, counter).R == 36 && capture.GetPixel(iconTopLeftX, counter).G == 36 && capture.GetPixel(iconTopLeftX, counter).B == 39)
                        {
                            counter--;
                            break;
                        }
                    }
                }
                //finding the width of the slot
                int iconWidth;
                counter = iconTopLeftX;
                while (true)
                {
                    counter++;
                    if (capture.GetPixel(counter, iconTopY).R == 36 && capture.GetPixel(counter, iconTopY).G == 36 && capture.GetPixel(counter, iconTopY).B == 39)
                    {
                        counter--;
                        iconWidth = counter - iconTopLeftX + 1;
                        break;
                    }
                }
                int[] hSteps = new int[7];
                previousLastPixel = iconTopLeftX;
                for (int i = 0; i < 7; i++)
                {
                    while (true)
                    {
                        counter++;
                        if (capture.GetPixel(counter, iconTopY).R != 36 || capture.GetPixel(counter, iconTopY).G != 36 || capture.GetPixel(counter, iconTopY).B != 39)
                        {
                            hSteps[i] = counter - previousLastPixel;
                            previousLastPixel = counter;
                            break;
                        }
                    }
                    while (true)
                    {
                        counter++;
                        if (capture.GetPixel(counter, iconTopY).R == 36 && capture.GetPixel(counter, iconTopY).G == 36 && capture.GetPixel(counter, iconTopY).B == 39)
                        {
                            counter--;
                            break;
                        }
                    }
                }
                //can now write all coordinates to file :D
                capture.Dispose();
                try
                {
                    lineEditor("Top left corner width (horizontal)" + ',' + Convert.ToString(iconTopLeftX + activeScreen.Bounds.Width / 2), Directory.GetCurrentDirectory() + "\\Position Settings.csv", 0);
                    lineEditor("Top left corner height (vertical)" + ',' + Convert.ToString(iconTopY), Directory.GetCurrentDirectory() + "\\Position Settings.csv", 1);
                    lineEditor("Horizontal steps to next slots" + ',' + Convert.ToString(hSteps[0]) + ',' + Convert.ToString(hSteps[1]) + ',' + Convert.ToString(hSteps[2]) + ',' + Convert.ToString(hSteps[3]) + ',' + Convert.ToString(hSteps[4]) + ',' + Convert.ToString(hSteps[5]) + ',' + Convert.ToString(hSteps[6]), Directory.GetCurrentDirectory() + "\\Position Settings.csv", 2);
                    lineEditor("Vertical steps to next slots" + ',' + Convert.ToString(vSteps[0]) + ',' + Convert.ToString(vSteps[1]) + ',' + Convert.ToString(vSteps[2]) + ',' + Convert.ToString(vSteps[3]) + ',' + Convert.ToString(vSteps[4]) + ',' + Convert.ToString(vSteps[5]) + ',' + Convert.ToString(vSteps[6]), Directory.GetCurrentDirectory() + "\\Position Settings.csv", 3);
                    lineEditor("Width of slot" + ',' + Convert.ToString(iconWidth), Directory.GetCurrentDirectory() + "\\Position Settings.csv", 4);
                    File.Copy(Directory.GetCurrentDirectory() + "\\Position Settings.csv", Directory.GetCurrentDirectory() + "\\Python\\Position Settings.csv", true);
                }
                catch
                {
                    MessageBox.Show("Could not write inventory location!\nPlease make sure the folder is not read-only and try again.", "IO error", MessageBoxButtons.OK, MessageBoxIcon.Error);
                }
                lineEditor("Has first alignment been done,True", Directory.GetCurrentDirectory() + "\\Position Settings.csv", 5);
                databaseLoad();
                return true;
            }
            catch
            {
                MessageBox.Show("Make sure the entire inventory and all slots are on-screen!\nPlease try again.", "Alignment failed", MessageBoxButtons.OK, MessageBoxIcon.Error);
                return false;
            }
        }

