        void getCurrentUIScale()
        {
            string[] temp = null;
            try
            {
                temp = File.ReadAllLines(Environment.GetFolderPath(Environment.SpecialFolder.MyDocuments) + "\\Black Desert\\GameOption.txt");
            }
            catch
            {
                MessageBox.Show("Could not find BDO Options!", "BDO GameOption missing", MessageBoxButtons.OK, MessageBoxIcon.Error);
                System.Environment.Exit(4);
            }
            string subtemp = temp[66].Substring(temp[66].Length - 4, 4);
            currentUIScale = Convert.ToInt32(subtemp.Split('.')[0] + subtemp.Split('.')[1]);
            if (currentUIScale != 100)
            {
                currentUIScale = 100;
                analyzeButton.Enabled = false;
                badScaleLabel.Visible = true;
                return;
            }
            if (digits[0] != null)
                for (int i = 0; i < 10; i++)
                    digits[i].Dispose();
            if (Convert.ToInt32(temp[101].Substring(temp[101].Length - 1, 1)) == 0)
                for (int i = 0; i < 10; i++)
                    digits[i] = (Bitmap)Image.FromFile(Directory.GetCurrentDirectory() + "\\Sorting Mode\\Digits\\DejaVu Sans\\" + Convert.ToString(currentUIScale) + "\\" + Convert.ToString(i) + ".bmp");
            else if (Convert.ToInt32(temp[101].Substring(temp[101].Length - 1, 1)) == 2)
                for (int i = 0; i < 10; i++)
                    digits[i] = (Bitmap)Image.FromFile(Directory.GetCurrentDirectory() + "\\Sorting Mode\\Digits\\Strong Sword\\" + Convert.ToString(currentUIScale) + "\\" + Convert.ToString(i) + ".bmp");
            if (currentUIScale != oldUIScale)
            {
                needsAlignment = true;
            }
            toolTip.SetToolTip(badScaleLabel, null);
            badScaleLabel.Visible = false;
            analyzeButton.Enabled = true;
        }

        public void changeAnalyzeButton(bool makeItRed)
        {
            if (makeItRed == true)
            {
                analyzeButton.BackColor = Color.Red;
                analyzeButton.FlatAppearance.MouseDownBackColor = Color.DarkRed;
                analyzeButton.FlatAppearance.MouseOverBackColor = Color.Firebrick;
                inventoryHighlights.Enabled = true;
                inventoryHighlights.Visible = true;
                toolTip.SetToolTip(analyzeButton, "The program will attempt to find your inventory's\r\nposition on the next Analysis. Please make sure\r\nyou don't have any animated icons on the marked\r\nedges of your inventory (i.e. Black Stones).\r\n");
            }
            else
            {
                analyzeButton.BackColor = Color.Pink;
                analyzeButton.FlatAppearance.MouseDownBackColor = Color.DeepPink;
                analyzeButton.FlatAppearance.MouseOverBackColor = Color.HotPink;
                inventoryHighlights.Enabled = false;
                inventoryHighlights.Visible = false;
                toolTip.SetToolTip(analyzeButton, null);
            }
        }

